mapper.update:
UPDATE TBL_USER
SET USER_PW = #{user_pw}, USER_EMAIL = #{user_email}, USER_PHONE_NUM = #{user_phone_num}
WHERE USER_ID = #{user_id}

mapper.select:
SELECT USER_ID, USER_PW
FROM TBL_USER
WHERE USER_ID = #{user_id} AND USER_PW = #{user_pw}

SELECT COUNT(*)
FROM TBL_USER
WHERE USER_ID = #{user_id}
